import React from "react";


class App extends React.Component {
  state = {
    temp: null,
    availableList: [],
    wishList: []
  }



  render() {
    return (
      <div>
        <h1>My Wish List</h1>
        <h3>Available Items</h3>
        <input placeholder='Type here' type="text" value={this.state.temp} onChange={event => (this.setState({ temp: event.target.value }))} />
        <button onClick={() => { this.setState({ availableList: [...this.state.availableList, this.state.temp] }, this.setState({ temp: '' })) }}>Add to Available</button>
        <ul>
          {this.state.availableList.map(item => {
            return (
              <div>
                <li>{item}
                  <button onClick={() => { this.setState({ wishList: [...this.state.wishList, item], availableList: this.state.availableList.filter(e => e !== item) }) }} >Add to Wish List</button></li>
              </div>
            )
          })}
        </ul>
        <h3>My wishList</h3>
        <ul>
          {this.state.wishList.map(item => {
            return (
              <div>
                <li>{item}
                  <button>Remove from Wish List</button></li>
              </div>
            )
          })}
        </ul>
      </div >
    )
  }
}

export default App
